import React, { useState, useEffect, memo } from 'react';

const Loader = memo(({ onLoadingComplete }) => {
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setProgress(prev => {
        if (prev >= 100) {
          clearInterval(timer);
          onLoadingComplete();
          return 100;
        }
        return prev + 1;
      });
    }, 20);

    return () => clearInterval(timer);
  }, [onLoadingComplete]);

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-softBg dark:bg-black">
      <div className="w-[80%] sm:w-64 space-y-3 sm:space-y-4">
        <div className="h-1 w-full bg-gray-100 dark:bg-gray-800 rounded-full overflow-hidden">
          <div 
            className="h-full bg-gradient-to-r from-blue via-purple to-pinkLight transition-all duration-300 ease-out"
            style={{ width: `${progress}%` }}
          />
        </div>
        <p className="text-center text-blue dark:text-blue font-light">
          {progress}%
        </p>
      </div>
    </div>
  );
});

export default Loader;
