import React, { memo, useEffect } from 'react';
import './Loader.css'; // Import the CSS for the ellipsis animation

const Loader = memo(({ onLoadingComplete }) => {
  useEffect(() => {
    const timer = setTimeout(onLoadingComplete, 1500); // Simulate loading for 1.5 seconds
    return () => clearTimeout(timer);
  }, [onLoadingComplete]);

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center" style={{ backgroundColor: 'var(--loader-bg-color)' }}>
      <div className="lds-ellipsis">
        <div></div>
        <div></div>
        <div></div>
        <div></div>
      </div>
    </div>
  );
});

export default Loader;
