import React, { useState, useEffect, useRef } from 'react';
import { motion, useMotionValue, useTransform, animate } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import catImg from '../assets/images/cat.png';

const NotFound = () => {
  const navigate = useNavigate();
  const [showMessage, setShowMessage] = useState(false);
  const containerRef = useRef(null);
  const [containerSize, setContainerSize] = useState({ width: 0, height: 0 });
  
  // Mouse parallax
  const mouseX = useMotionValue(0);
  const mouseY = useMotionValue(0);
  
  // Stars parallax
  const starsX = useTransform(mouseX, [-500, 500], [50, -50]);
  const starsY = useTransform(mouseY, [-500, 500], [50, -50]);
  
  // Planets parallax (slower movement)
  const planetsX = useTransform(mouseX, [-500, 500], [20, -20]);
  const planetsY = useTransform(mouseY, [-500, 500], [20, -20]);

  // Cat position and velocity
  const x = useMotionValue(Math.random() * 200);
  const y = useMotionValue(Math.random() * 200);
  const velocityX = useRef(1); // Reduced from 1.5 to 1 for slower movement
  const velocityY = useRef(1); // Reduced from 1.5 to 1 for slower movement
  const catSize = {
    width: window.innerWidth < 768 ? 96 : 128, // 96px for mobile, 128px for desktop
    height: window.innerWidth < 768 ? 96 : 128
  };

  // Update these values for better boundary control
  useEffect(() => {
    const handleResize = () => {
      // Update cat size on resize
      catSize.width = window.innerWidth < 768 ? 96 : 128;
      catSize.height = window.innerWidth < 768 ? 96 : 128;
      
      // Update container size with safe padding
      if (containerRef.current) {
        const padding = 60; // Increased padding from 40 to 60
        setContainerSize({
          width: Math.max(0, containerRef.current.offsetWidth - catSize.width - padding),
          height: Math.max(0, containerRef.current.offsetHeight - catSize.height - padding)
        });
      }
    };

    handleResize(); // Initial call
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    let isAnimating = true;

    const updatePosition = () => {
      if (!isAnimating) return;

      const newX = x.get() + velocityX.current;
      const newY = y.get() + velocityY.current;

      // Add more padding to boundaries
      const padding = 30;
      const minX = padding;
      const minY = padding;
      const maxX = Math.max(padding, containerSize.width - padding);
      const maxY = Math.max(padding, containerSize.height - padding);

      // Ensure we're within valid bounds
      if (newX <= minX || newX >= maxX) {
        velocityX.current *= -1;
        // Add some randomness to the bounce
        velocityY.current += (Math.random() - 0.5) * 0.5;
        animate(x, newX <= minX ? minX : maxX, {
          type: "spring",
          stiffness: 100,
          damping: 8
        });
      }

      if (newY <= minY || newY >= maxY) {
        velocityY.current *= -1;
        // Add some randomness to the bounce
        velocityX.current += (Math.random() - 0.5) * 0.5;
        animate(y, newY <= minY ? minY : maxY, {
          type: "spring",
          stiffness: 100,
          damping: 8
        });
      }

      // Clamp values to ensure cat stays in bounds
      x.set(Math.max(minX, Math.min(maxX, newX)));
      y.set(Math.max(minY, Math.min(maxY, newY)));

      // Keep velocity in check
      velocityX.current = Math.max(-1.5, Math.min(1.5, velocityX.current));
      velocityY.current = Math.max(-1.5, Math.min(1.5, velocityY.current));

      requestAnimationFrame(updatePosition);
    };

    requestAnimationFrame(updatePosition);
    const timer = setTimeout(() => setShowMessage(true), 800);

    return () => {
      isAnimating = false;
      clearTimeout(timer);
    };
  }, [containerSize, x, y]);

  return (
    <div className="h-screen w-screen bg-[#0F172A] overflow-hidden relative" ref={containerRef}>
      {/* Stars Layer */}
      <motion.div 
        className="absolute inset-0"
        style={{ x: starsX, y: starsY }}
      >
        {/* Generate twinkling stars */}
        {Array.from({ length: 50 }).map((_, i) => (
          <motion.div
            key={i}
            className="absolute w-1 h-1 bg-white rounded-full"
            animate={{
              opacity: [0.2, 0.8, 0.2],
              scale: [1, 1.2, 1],
            }}
            transition={{
              duration: 2 + Math.random() * 2,
              repeat: Infinity,
              delay: Math.random() * 2,
            }}
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
          />
        ))}
      </motion.div>

      {/* Planets Layer */}
      <motion.div 
        className="absolute inset-0"
        style={{ x: planetsX, y: planetsY }}
      >
        {/* Add floating planets */}
        <div className="absolute top-[20%] left-[15%] text-8xl opacity-50">ü™ê</div>
        <div className="absolute bottom-[30%] right-[20%] text-6xl opacity-30">üåç</div>
      </motion.div>

      {/* Main Content */}
      <div className="relative z-10 h-full flex flex-col items-center justify-center px-4">
        {/* 404 Text */}
        <div className="mb-8">
          {"404".split("").map((num, i) => (
            <motion.span
              key={i}
              className="inline-block text-8xl md:text-9xl font-light text-white/90"
              animate={{
                y: [0, -10, 0],
              }}
              transition={{
                duration: 2,
                delay: i * 0.2,
                repeat: Infinity,
                ease: "easeInOut"
              }}
              whileHover={{ scale: 1.1, rotate: [-5, 5, 0] }}
            >
              {num}
            </motion.span>
          ))}
        </div>

        {/* Bouncing Cat */} 
        <motion.div
          className="absolute flex items-center justify-center w-full h-full"
          animate={{
            y: [-10, 10, -10], // Floating motion
          }}
          transition={{
            duration: 3,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        >
          <motion.img
            src={catImg}
            alt="Lost Cat"
            className="w-24 h-24 md:w-32 md:h-32 select-none"
            animate={{
              rotate: [0, 5, -5, 0], // Slight rotation
            }}
            transition={{
              duration: 6,
              repeat: Infinity,
              ease: "linear",
            }}
            draggable={false}
          />
        </motion.div>

        {/* Message */}
        <motion.div
          className="mt-8 text-center"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: showMessage ? 1 : 0, y: showMessage ? 0 : 20 }}
        >
          <h2 className="text-2xl md:text-3xl text-white/90 mb-2">
            lost in space ü™ê
          </h2>
          <p className="text-white/70 mb-8">
            the page you're looking for drifted into a black hole.
          </p>
          
          {/* Return Button */}
          <motion.button
            onClick={() => navigate('/')}
            className="px-8 py-3 bg-white/10 backdrop-blur-md rounded-full
                     border border-white/20 text-white/90 hover:text-white
                     transition-all duration-300 group relative overflow-hidden"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <span className="relative z-10">return to home base üöÄ</span>
            <motion.div
              className="absolute inset-0 bg-gradient-to-r from-purple-500/20 to-blue-500/20"
              initial={{ x: '-100%' }}
              whileHover={{ x: '100%' }}
              transition={{ duration: 0.5 }}
            />
          </motion.button>
        </motion.div>
      </div>
    </div>
  );
};

export default NotFound;
